<?phpif(!defined('IN_DISCUZ') || !defined('IN_ADMINCP')) {	exit('Access Denied');}$plugin_zhikai_smslog = DB::table('zhikai_smslog');$plugin_zhikai_postnotice = DB::table('zhikai_postnotice');$plugin_zhikai_userdata = DB::table('zhikai_userdata');$sql = <<<EOFDROP TABLE IF EXISTS $plugin_zhikai_smslog;DROP TABLE IF EXISTS $plugin_zhikai_postnotice;DROP TABLE IF EXISTS $plugin_zhikai_userdata;EOF;runquery($sql);function zhikai_sms($directory, $empty = false) {    if(substr($directory,-1) == "/") {        $directory = substr($directory,0,-1);    }    if(!file_exists($directory) || !is_dir($directory)) {        return false;    } elseif(!is_readable($directory)) {        return false;    } else {        @$directoryHandle = opendir($directory);        while ($contents = @readdir($directoryHandle)) {            if($contents != '.' && $contents != '..') {                $path = $directory . "/" . $contents;                if(is_dir($path)) {                    @zhikai_sms($path, $empty);                } else {                    @unlink($path);                }            }        }        @closedir($directoryHandle);        if($empty == false) {            if(!@rmdir($directory)) {                return false;            }        }        return true;    }}zhikai_sms(DISCUZ_ROOT.'./source/plugin/zhikai_sms');@unlink(DISCUZ_ROOT.'./data/sysdata/cache_zhikai_sms.php');$finish = TRUE;?>